sp_widget.client_script:
function($rootScope,$scope,spUtil) {
	/* widNoteget controller */
	var c = this;	
	c.selectItem = function(idx) {
		var idNote = c.data.notes[idx].sys_id;
		$rootScope.noteID = idNote;
		$rootScope.$emit('selectNote', idNote);
	}

	$rootScope.$on('updateRow',function(event){
		c.server.update().then(function(r){
			console.log('$rootScope',$rootScope)
		})
	})

}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
  data.notes = [];

  var getNotes = new  GlideRecord('x_snc_createnotes_note');
  getNotes.addQuery('user',gs.getUser().getID());
  getNotes.orderByDesc('sys_created_on');
  getNotes.query();

  while(getNotes.next()){
    var objectProperties  = {};
    $sp.getRecordDisplayValues(objectProperties,getNotes,'number,title,sys_id');
    objectProperties.note = getNotes.getValue('note').slice(0,20);
    data.notes.push(objectProperties);
  }

})();
